import React, { useRef, useState, useEffect } from "react";
import { Row, Col, Carousel, Form } from "react-bootstrap";

import { StatsCard } from "components/StatsCard/StatsCard.jsx";

import BaseService from "../services/base.services";

export default function Dashboard() {
  const basrUrlImage = "https://image.tmdb.org/t/p/original/";
  const [banners, setBanners] = useState([]);
  const [jogos, setJogos] = useState([]);

  useEffect(() => {
    BaseService.get("trending").then(response => {
      setBanners(JSON.parse(response.data).results);
    });
  }, []);

  useEffect(() => {
    console.log(banners);
  }, [banners]);

  return (
    <div className="content">
      <Row>
        <Col lg={12}>
          <Carousel>
            <Carousel.Item>
              <img
                className="d-block w-100"
                src="holder.js/800x400?text=First slide&bg=373940"
                alt="First slide"
              />
              <Carousel.Caption>
                <h3>First slide label</h3>
                <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
              </Carousel.Caption>
            </Carousel.Item>
            <Carousel.Item>
              <img
                className="d-block w-100"
                src="holder.js/800x400?text=Second slide&bg=282c34"
                alt="Third slide"
              />

              <Carousel.Caption>
                <h3>Second slide label</h3>
                <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
              </Carousel.Caption>
            </Carousel.Item>
            <Carousel.Item>
              <img
                className="d-block w-100"
                src="holder.js/800x400?text=Third slide&bg=20232a"
                alt="Third slide"
              />

              <Carousel.Caption>
                <h3>Third slide label</h3>
                <p>Praesent commodo cursus magna, vel scelerisque nisl consectetur.</p>
              </Carousel.Caption>
            </Carousel.Item>
          </Carousel>
        </Col>
        <Col lg={12} sm={12}>
          {banners.length > 0 && (
            <Carousel style={{ width: "100%", height: 400 }}>
              {banners.map(banner => (
                <Carousel.Item style={{ width: "100%", height: 400 }} key={banner.id}>
                  <img
                    className="d-block w-100"
                    src={basrUrlImage + banner.backdrop_path}
                    alt={banner.original_title}
                  />
                  <Carousel.Caption>
                    <h3>{banner.original_title}</h3>
                    <p>{banner.overview}</p>
                  </Carousel.Caption>
                </Carousel.Item>
              ))}
            </Carousel>
          )}
        </Col>
        <Col lg={12} sm={12}>
          <Form.Group>
            <Form.Control size="lg" type="text" placeholder="Large text" />
            <br />
          </Form.Group>
        </Col>
      </Row>
    </div>
  );
}
