import React, { useRef, useState, useEffect } from "react";
import { Row, Col, Carousel } from "react-bootstrap";

import { StatsCard } from "components/StatsCard/StatsCard.jsx";

import BaseService from "../services/base.services";

export default function Dashboard() {
  const [banners, setBanners] = useState([]);
  const [jogos, setJogos] = useState([]);

  useEffect(() => {
    BaseService.get("trending").then(reponse => {
      setBanners(response);
    });
  }, []);

  useEffect(() => {
    console.log(banners);
  }, [banners]);

  return (
    <div className="content">
      <Row>
        <Col lg={12} sm={12}>
          <Carousel>
            <Carousel.Item>
              <img
                className="d-block w-100"
                src="holder.js/800x400?text=First slide&bg=373940"
                alt="First slide"
              />
              <Carousel.Caption>
                <h3>First slide label</h3>
                <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
              </Carousel.Caption>
            </Carousel.Item>
            <Carousel.Item>
              <img
                className="d-block w-100"
                src="holder.js/800x400?text=Second slide&bg=282c34"
                alt="Third slide"
              />

              <Carousel.Caption>
                <h3>Second slide label</h3>
                <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
              </Carousel.Caption>
            </Carousel.Item>
            <Carousel.Item>
              <img
                className="d-block w-100"
                src="holder.js/800x400?text=Third slide&bg=20232a"
                alt="Third slide"
              />

              <Carousel.Caption>
                <h3>Third slide label</h3>
                <p>Praesent commodo cursus magna, vel scelerisque nisl consectetur.</p>
              </Carousel.Caption>
            </Carousel.Item>
          </Carousel>
        </Col>
        <Col lg={3} sm={6}>
          <StatsCard
            bigIcon={<i className="fa fa-play text-success" />}
            statsText="Jogos"
            statsValue={jogos.length > 0 ? jogos.length : 0}
            statsIcon={<i className="fa fa-refresh" />}
            statsIconText="Confira sua lista de jogos"
          />
        </Col>
        <Col lg={3} sm={6}>
          <StatsCard
            bigIcon={<i className="fa fa-close text-danger" />}
            statsText="Jogos Emprestados"
            statsValue={jogos.filter(x => x.Amigo != null).length > 0 ? jogos.filter(x => x.Amigo != null).length : 0}
            statsIcon={<i className="fa fa-refresh" />}
            statsIconText="Veja os jogos que você emprestou"
          />
        </Col>
        <Col lg={3} sm={6}>
          <StatsCard
            bigIcon={<i className="fa fa-check text-info" />}
            statsText="Jogos Disponíveis"
            statsValue={jogos.filter(x => x.Amigo == null).length > 0 ? jogos.filter(x => x.Amigo == null).length : 0}
            statsIcon={<i className="fa fa-refresh" />}
            statsIconText="Veja os jogos que estão disponíveis para jogar"
          />
        </Col>
      </Row>
    </div>
  );
}
