import Card from 'react-bootstrap/Card'
import Breadcrumb from 'react-bootstrap/Breadcrumb'
import 'bootstrap/dist/css/bootstrap.min.css';


import BaseService from "../services/base.services";

export default function Discover() {
  const basrUrlImage = "https://image.tmdb.org/t/p/original/";
  const [generos, setGeneros] = useState([]);
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    BaseService.get("genres").then(response => {
      setGeneros(JSON.parse(response.data).genres);
    });
  }, []);

  useEffect( () => {

  }, [generos]);

  const handleMoviesbyGenres = (genreId) => {
    BaseService.get(`discover?genres=${genreId}`).then(response => {
      return response.data;
    });
  }

  return (
    <div className="content">
      <Row>
        {generos.length > 0 && (
          <Col lg={12} sm={12}>
            {generos.map(genero => {
              handleMoviesbyGenres(genero.id).then(movie => {
