import React, { useRef, useState, useEffect } from "react";
import { Row, Col, Form } from "react-bootstrap";
import Card from 'react-bootstrap/Card'
import Breadcrumb from 'react-bootstrap/Breadcrumb'
import 'bootstrap/dist/css/bootstrap.min.css';


import BaseService from "../services/base.services";

export default function Discover() {
  const basrUrlImage = "https://image.tmdb.org/t/p/original/";
  const [generos, setGeneros] = useState([]);
  const [movies, setMovies] = useState(null);
  const [loadGenreMovies, setLoadGenreMovies] = useState(false);
  const [genreMovies, setGenreMovies] = useState(new Object);


  const handleMoviesGenre = async (genreId) => {
    BaseService.get(`discover?genres=${genreId}`).then(response => {
      console.log(response.data);
    });
  }

  useEffect(() => {
    BaseService.get("genres").then(response => {
      setGeneros(JSON.parse(response.data).genres);
    });
  }, []);

  useEffect(() => {
    if (generos.length > 0) {
      generos.map((genero => {
        genreMovies[genero.id] = new Object;
      }));
      setGenreMovies(genreMovies);
      setLoadGenreMovies(true);
    }
  }, [generos]);

  useEffect(() => {
    generos.map(genero => {
      BaseService.get(`discover?genres=${genero.id}`).then(response => {
        genreMovies[genero.id] = response.data.results;
        setMovies(genreMovies);
      });
    });
  }, [loadGenreMovies]);

  useEffect(() => {
    console.log("filmes", movies);
  }, [movies]);

  return (
    <div className="content">
      <Row>
        {generos.length && movies.length > 10 > 0 && (
          <Col lg={12} sm={12}>
            {generos.map(genero => {
              console.log(movies);
              if (movies && movies[genero.id] && movies[genero.id].length > 0) {
                return (
                  <Breadcrumb key={genero.id}>
                    <Breadcrumb.Item href="#">{genero.name}</Breadcrumb.Item>
                  </Breadcrumb>

                );
              } else {
                return <></>
              }
            })}
          </Col>
        )}
      </Row>
    </div>
  );
}
